import {Fragment, useState, useEffect} from 'react'
import ListExperience from './listExperience'

const Experience = () => {
  const [experienceType, setExperienceType] = useState(null)
  const [listExperience, setListExperience] = useState(null)

  useEffect(() => {
    setListExperience(<ListExperience experienceType={experienceType} />)
  }, [experienceType])

  const handleChooseExperience = (e) => {
    setExperienceType(e.target.id)
  }

  return (
    <Fragment>
      <h1>Choose your experience</h1>
      <button onClick={(e) => handleChooseExperience(e)} id='insideExperience'>Interieur</button>
      <button onClick={(e) => handleChooseExperience(e)} id='outdoorExperience'>Exterieur</button>
      {listExperience}
    </Fragment>
  )
}

export default Experience
