import { Fragment } from 'react'
import { useHistory } from "react-router-dom";
import ListExperience from './listExperience'

const Experience = () => {
  const history = useHistory()

  const handleChooseExperience = (e) => {
    const experienceType = e.target.id
    history.push(`experience/${experienceType}`)
  }

  return (
    <Fragment>
      <h1>Choose your experience</h1>
      <button onClick={(e) => handleChooseExperience(e)} id='inside'>Interieur</button>
      <button onClick={(e) => handleChooseExperience(e)} id='outdoor'>Exterieur</button>
    </Fragment>
  )
}

export default Experience
